/*while Crie um programa que imprima 11 vezes a frase " Hello World!"
utilizando uma estrutura de repetição while.*/

package parteDois;

public class ImpressaoHelloWorld {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int contador = 0;

		while (contador < 11) {
			contador++; //contador = contador + 1
			System.out.println(contador + " Hello world! ");

		}

	}

}
